import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import BlogPostComponent from '../components/BlogPost';
import { TextForFirstImage, TextForSecondImage, TextForThirdImage, TextForFourthImage, TextForFifthImage, TextForSixImage } from '../imageTexts';

// Contenu des articles
const posts = {
  "premier-article": `
    # Phénomène Météorologique
    Différents phénomène météorologique
  `,
  "deuxieme-article": `
    # Monstre Mythologique
    Différents animaux mythologique protecteur
  `,
};

// Images et textes associés
const imageDetails = {
  "premier-article": [
    {
      src: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTfs9OJo0-e_GiM-D2IOYbM8b2O0W84Z9DuMQ&s",
      alt: "First",
      text: <TextForFirstImage />,
    },
    {
      src: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTqsXXdG3dUY9vv9ll3YBX-Xrmyl9vSiYmra1o_zLTRrFrWEy89zQhR9wYG8f8tUmUWBm8&usqp=CAU",
      alt: "Second",
      text: <TextForSecondImage />,
    },
    {
      src: "data:image/jpeg;base64,",
      alt: "Third",
      text: <TextForThirdImage />,
    },
  ],
  "deuxieme-article": [
    {
      src: "data:image/jpeg;base64,",
      alt: "Fourth",
      text: <TextForFourthImage />,
    },
    {
      src: "data:image/jpeg;base64,",
      alt: "Fifth",
      text: <TextForFifthImage />,
    },
    {
      src: "data:image/jpeg;base64,",
      alt: "Six",
      text: <TextForSixImage />,
    },
  ],
};

const BlogPost = () => {
  const { slug } = useParams();
  const content = posts[slug];
  const [selectedImage, setSelectedImage] = useState(null);

  if (!content) {
    return <div>Article non trouvé</div>;
  }

  const handleImageClick = (imageSrc) => {
    const imageDetail = imageDetails[slug].find(img => img.src === imageSrc);
    setSelectedImage({ src: imageSrc, text: imageDetail.text });
  };

  const handleCloseModal = () => {
    setSelectedImage(null);
  };

  const renderImages = () => {
    return imageDetails[slug].map((image, index) => (
      <div className="relative group" key={index}>
        <img
          src={image.src}
          style={{ width: '250px', height: '250px' }}
          alt={image.alt}
          className="transition-transform duration-300 transform group-hover:scale-110"
          onClick={() => handleImageClick(image.src)}
        />
      </div>
    ));
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <Link to="/" className="text-blue-600 hover:underline mb-4 block">
        ← Retour à l'accueil
      </Link>
      <BlogPostComponent content={content} />

      <div className="flex justify-between gap-4">
        {renderImages()}
      </div>

      {selectedImage && (
        <div className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50">
          <div className="bg-white p-4 rounded shadow-lg w-full h-full flex flex-col items-center justify-center">
            <img src={selectedImage.src} alt="Selected" className="max-w-full max-h-full mb-4" />
            {selectedImage.text}
            <button onClick={handleCloseModal} className="mt-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">
              Fermer
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default BlogPost;